package org.ip.sesion01;

public class OperadoresUnarios {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 8;
		int a, b, c;
		/*
		 * para comenzar i vale 8, y definimos 3 variables para ver que les
		 * ocurre al incrementar o decrementar i y si lo hacemos antes o despues
		 * de asignarle valores a las variables.
		 */
		System. out.println("\tantes\tdurante\tdespués");
		/****************************************************************************/
		i = 8;
		a = i;
		b = i++;
		c = i;
		/*
		 * primer paso:
		 * si i = 8, y a = i, a debe valer 8; para b le asignamos el valor de i, y justo
		 * despues incrementamos este, asi que toma el valor sin el incremento b = 8; y c
		 * toma el valor de i pero esta viene incrementada del paso anterior, así que c = 9.
		 */
		System. out.println("i++\t" + a + " \t" + b + " \t" + c);
		/****************************************************************************/
		
		i = 8;
		a = i;
		b = i--;
		c = i;
		/*
		 * segundo paso:
		 * i vuelve a valer 8, a vuelve a valer i a = 8; b toma el valor de i de nuevo 
		 * antes del decremento de esta b = 8; y c toma el valor de i decrementado 
		 * del paso anterior c = 7.
		 */
		System. out.println("i--\t" + a + " \t" + b + " \t" + c);
		/****************************************************************************/
		
		i = 8;
		a = i;
		b = ++i;
		c = i;
		/*
		 * tercer paso:
		 * i de nuevo vale 8, a sigue valiendo i a = 8; b ahora toma su valor después
		 * del incremento de i, b = 9; y c toma su valor de i incrementada, c = 9.
		 */
		System. out.println("++i\t" + a + " \t" + b + " \t" + c);
		/****************************************************************************/
		
		i = 8;
		a = i;
		b = --i;
		c = i;
		/*
		 * cuarto paso:
		 * le asignamos de nuevo a i el valor 8, a igualmente es asignado con el valor
		 * de i sin tocar, a = 8; b es asignado con el valor decrementado de i, b = 7;
		 * y c toma el valor decrementado de i del paso anterior, c = 7.
		 */
		System. out.println("--i\t" + a + " \t" + b + " \t" + c);
	}

}
